mapper.select:
select d.dkTime as 'date',d.dkTime,u.* from dk d,user u where u.userId=d.userId

    and u.userId=#{userId}


    and u.ks=#{ks}

mapper.update:
update set userId=#{userId}

    ,userName=#{userName}


    ,password=#{password}


    ,permission=#{permission}


    ,ks=#{ks}


    ,phone=#{phone}


    ,xb=#{xb}

from user where userId=#{userId}
